"use strict";function detect_url(tab){var github_regex=/^https?:\/\/github.com\/(.+)/i;var github_pages_regex=/^https?:\/\/(.+).github.io\/(.*)/i;if(tab.url.match(github_regex)){var path=tab.url.match(github_regex)[1];var username=path.split("/")[0];var project_name=path.split("/")[1];var site_name="GitHub";var username_link=github_invalid_username_paths.includes(username)||!project_name&&is_github_404(tab.title)?undefined:"https://github.com/".concat(username);var project_link=!project_name||is_github_404(tab.title)||is_github_500(tab.title)?undefined:"https://github.com/".concat(username,"/").concat(project_name);return{site_name:site_name,username:username,username_link:username_link,project_name:project_name,project_link:project_link}}else if(tab.url.match(github_pages_regex)){var _username=tab.url.match(github_pages_regex)[1];var _username_link="https://github.com/".concat(_username);var _site_name="GitHub Pages";var project_path=tab.url.match(github_pages_regex)[2];var _project_name;var _project_link;if(is_github_pages_404(tab.title))_project_link;else if(!project_path){_project_name="".concat(_username,".github.io");_project_link="https://github.com/".concat(_username,"/").concat(_project_name)}else{_project_name=project_path.split("/")[0];_project_link="https://github.com/".concat(_username,"/").concat(_project_name)}return{site_name:_site_name,username:_username,username_link:_username_link,project_name:_project_name,project_link:_project_link}}else return}
